sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Determine Telugu grade based on marks
    var x = parseInt(g_form.getValue('telugu')) || 0;

    if (x >= 90) {
        g_form.setValue('telugu_percentage', 'A1');
    } 
    else if (x >= 80) {
        g_form.setValue('telugu_percentage', 'A2');
    } 
    else if (x >= 70) {
        g_form.setValue('telugu_percentage', 'B1');
    } 
    else if (x >= 60) {
        g_form.setValue('telugu_percentage', 'B2');
    } 
    else if (x >= 50) {
        g_form.setValue('telugu_percentage', 'C1');
    } 
    else if (x >= 40) {
        g_form.setValue('telugu_percentage', 'C2');
    } 
    else if (x >= 35) {
        g_form.setValue('telugu_percentage', 'D');
    } 
    else {
        g_form.setValue('telugu_percentage', 'F');
    }
}
