sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading) {
        return;
    }

    // Parse each subject's marks safely
    var te = parseInt(g_form.getValue('telugu')) || 0;
    var h = parseInt(g_form.getValue('hindi')) || 0;
    var e = parseInt(g_form.getValue('english')) || 0;
    var m = parseInt(g_form.getValue('maths')) || 0;
    var s = parseInt(g_form.getValue('science')) || 0;
    var so = parseInt(g_form.getValue('social')) || 0;

    // Calculate total and average
    var total = te + h + e + m + s + so;
    g_form.setValue('total', total);
}
