sys_script_client.script:
function onLoad() {
    // Make calculated fields read-only
    g_form.setDisabled('total', true);
    g_form.setDisabled('average', true);
    g_form.setDisabled('percentage', true);
    g_form.setDisabled('total_grade', true);
    g_form.setDisabled('result', true);

    g_form.setDisabled('telugu_percentage', true);
    g_form.setDisabled('hindi_percentage', true);
    g_form.setDisabled('english_percentage', true);
    g_form.setDisabled('maths_percentage', true);
    g_form.setDisabled('science_percentage', true);
    g_form.setDisabled('social_percentage', true);
}
