sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var ins=g_form.getValue('installment_type');
   if(newValue){

	if(ins=='term'){
		g_form.setDisplay('i_term',true);
		g_form.setDisplay('ii_term',true);
		g_form.setDisplay('iii_term',true);
		g_form.setDisplay('iv_term',true);

		g_form.setDisplay('june',false);
		g_form.setDisplay('july',false);
		g_form.setDisplay('august',false);
		g_form.setDisplay('september',false);
		g_form.setDisplay('october',false);
		g_form.setDisplay('november',false);
		g_form.setDisplay('december',false);
		g_form.setDisplay('january',false);
		g_form.setDisplay('febraury',false);
		g_form.setDisplay('march',false);
		g_form.setDisplay('april',false);
	}
	else{
		g_form.setDisplay('i_term',false);
		g_form.setDisplay('ii_term',false);
		g_form.setDisplay('iii_term',false);
		g_form.setDisplay('iv_term',false);

		g_form.setDisplay('june',true);
		g_form.setDisplay('july',true);
		g_form.setDisplay('august',true);
		g_form.setDisplay('september',true);
		g_form.setDisplay('october',true);
		g_form.setDisplay('november',true);
		g_form.setDisplay('december',true);
		g_form.setDisplay('january',true);
		g_form.setDisplay('febraury',true);
		g_form.setDisplay('march',true);
		g_form.setDisplay('april',true);


	}
   }

}
