sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Fetch the record reference asynchronously
    g_form.getReference('admission_number', function(ve) {
        if (!ve) return; // safety check

        // Set the form values based on the reference
        g_form.setValue('admin_date', ve.admin_date);
        g_form.setValue('student_name', ve.student_name);
        g_form.setValue('grade', ve.grade);
        g_form.setValue('father_name', ve.father_name);
        g_form.setValue('father_cell', ve.father_cell);
        g_form.setValue('mother_name', ve.mother_name);
        g_form.setValue('mother_cell', ve.mother_cell);

        // Disable the fields to make them read-only
        var fields = ['admin_date', 'student_name', 'grade', 'father_name', 'father_cell', 'mother_name', 'mother_cell'];
        fields.forEach(function(f) {
            g_form.setDisabled(f, true);
        });
    });
}
