sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Fetch the admission record
    g_form.getReference('admission_number', function(ad) {
        if (!ad || !ad.grade) {
            return;
        }

        var grade = ad.grade;

        switch (grade) {
            case 'NURSARY':
                g_form.setValue('fee', "15000");
                break;
            case 'LKG':
                g_form.setValue('fee', "20000");
                break;
            case 'UKG':
                g_form.setValue('fee', "25000");
                break;
            case '1':
                g_form.setValue('fee', "30000");
                break;
            case '2':
                g_form.setValue('fee', "35000");
                break;
            case '3':
                g_form.setValue('fee', "40000");
                break;
            case '4':
                g_form.setValue('fee', "45000");
                break;
            case '5':
                g_form.setValue('fee', "50000");
                break;
            case '6':
                g_form.setValue('fee', "55000");
                break;
            case '7':
                g_form.setValue('fee', "60000");
                break;
            case '8':
                g_form.setValue('fee', "65000");
                break;
            case '9':
                g_form.setValue('fee', "70000");
                break;
            case '10':
                g_form.setValue('fee', "75000");
                break;
            default:
                g_form.setValue('fee', "");
        }
    });
}
