sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   // Get average as a number
   var avg = parseFloat(g_form.getValue('average')) || 0;

   // If average is a fraction (0-1), convert to percentage
   var percentage = avg;
   if (percentage <= 1) {
       percentage = avg * 100;
   }

   // Determine total grade
   if (percentage >= 90) {
       g_form.setValue('total_grade', 'A1');
   }
   else if (percentage >= 80) {
       g_form.setValue('total_grade', 'A2');
   }
   else if (percentage >= 70) {
       g_form.setValue('total_grade', 'B1');
   }
   else if (percentage >= 60) {
       g_form.setValue('total_grade', 'B2');
   }
   else if (percentage >= 50) {
       g_form.setValue('total_grade', 'C1');
   }
   else if (percentage >= 40) {
       g_form.setValue('total_grade', 'C2');
   }
   else if (percentage >= 35) {
       g_form.setValue('total_grade', 'D');
   }
   else {
       g_form.setValue('total_grade', 'F');
   }
}
