sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var fee = parseInt(g_form.getValue('fee'));
    var type = g_form.getValue('installment_type');

    if (!fee || fee <= 0) {
        return;
    }

    // Quarterly installments
    if (type === 'term') {
        var term = fee / 4;
        g_form.setValue('i_term', term);
        g_form.setValue('ii_term', term);
        g_form.setValue('iii_term', term);
        g_form.setValue('iv_term', term);
    } 
    // Monthly installments from June to April (11 months)
    else if (type === 'onth') {
        var monthly = fee / 11;
        var months = ['june', 'july', 'august', 'september', 'october', 'november', 'december', 'january', 'february', 'march', 'april'];
        for (var i = 0; i < months.length; i++) {
            g_form.setValue(months[i], monthly);
        }
    }
}
